def request1(twitter)
    puts "Nbre de Handle : #{twitter.length}"
end

def request2(twitter)
    twitter.sort_by!(&:length)
    puts "Handle le plus court : #{twitter[0]}"
end

def request3(twitter)
    # création du tableau  "stringLength"  contenant la taille de chaque Handle (-1 pour éliminer le caractère "@")
    stringLength = twitter.map do |x| 
        x.length-1
    end
    y = 0                                # initialisiation de y à 0
    (0..stringLength.length).each do |i| # Boucle de 0 à la longueur du tableau à parcourir
        if stringLength[i] == 5          # si ma case contient 5 
            y += 1                       # j'incrémente y
        end
    end
    puts "Nbre de Handle contenants 5 caractères : #{y}"
end

def request4(twitter)
                 # grep va filtrer les handle qui contiennent @ + un Majuscule et count va les compter
    puts "Nbre de Handle commençants par une Majuscule :  #{twitter.grep(/@[A-Z]/).count}"
end

def request5(twitter)
    puts "Liste triée par ordre Alphabétique : "
    puts
    tri = twitter.map(&:downcase)
    print tri.sort #sort va trier par ordre alphabétique croissant
    puts
end

def request6(twitter)
    puts "Liste triée par taille croissante des Handle : "
    puts
    puts "#{twitter.sort_by(&:length)}"
end

def request7(twitter)
    puts "Position dans l'array de la personne @epenser : #{twitter.index "@epenser"}"
end

def request8(twitter)
    twitter.sort_by!(&:length) #tri du tableau par longueur de chaine de caractere

    twitter2 = twitter.map do |n| 
        n.length        # on crée un tableau contenant la valeur des longueurs
    end

    compteur = 0
    refBase = twitter2[0]
    x = twitter2.length

    for i in 0...x
            if refBase != twitter2[i]
                puts "Handle size = #{refBase} total = #{compteur}"
                refBase = twitter2[i] # réinitialise le taille de référence
                compteur = 0   # on réinitialise le compteur
            end 
            compteur += 1
    end
    puts "Handle size = #{refBase} total = #{compteur}"
    puts
    puts
end

