#_________________________request1______________________________
def request1(twitter)
    puts "\n Nbre de Handle : #{twitter.length}\n"
end

#_________________________request2______________________________
def request2(twitter)
    twitter.sort_by!(&:length)
    puts "\n Handle le plus court : #{twitter[0]}\n"
end

#_________________________request3______________________________
def request3(twitter)
                                         # création du tableau  "stringLength"
    stringLength = twitter.map do |x|    # contenant la taille de chaque Handle (-1 pour éliminer le caractère "@")
        x.length-1
    end
    y = 0                                # initialisiation de y à 0
    (0..stringLength.length).each do |i| # Boucle de 0 à la longueur du tableau à parcourir
        if stringLength[i] == 5          # si ma case contient 5 
            y += 1                       # j'incrémente y
        end
    end
    puts "\n Nbre de Handle contenants 5 caractères : #{y}\n"
end

#__________________________request4_____________________________
def request4(twitter)
                                    # grep va filtrer les handle qui contiennent @ + un Majuscule et count va les compter
    puts "\n Nbre de Handle commençants par une Majuscule :  #{twitter.grep(/[UPPERCASE]/).count}\n"
end

#__________________________request5_____________________________
def request5(twitter)
    puts "\n Liste triée par ordre Alphabétique : \n"
    tri = twitter.map(&:downcase)
    print tri.sort                  # sort va trier par ordre alphabétique croissant
    puts
end

#__________________________request6_____________________________
def request6(twitter)
    puts "\n Liste triée par taille croissante des Handle : "
    puts "#{twitter.sort_by(&:length)}\n"
end

#__________________________request7_____________________________
def request7(twitter)
    puts "\n Position dans l'array de la personne @epenser : #{twitter.index "@epenser"}\n"
end

#__________________________request8_____________________________
def request8(twitter)
    twitter.sort_by!(&:length)      # tri du tableau par longueur de chaine de caractere
    twitter2 = twitter.map do |n| 
        n.length                    # on crée un tableau contenant la valeur des longueurs
    end
    compteur = 0
    refBase = twitter2[0]
    x = twitter2.length
    for i in 0...x
        if refBase != twitter2[i]
            puts " Handle size = #{refBase} total = #{compteur}"
            refBase = twitter2[i]   # réinitialise le taille de référence
            compteur = 0            # on réinitialise le compteur
        end 
        compteur += 1
    end
    puts " Handle size = #{refBase} total = #{compteur}\n"
end

#___________________________separe________________________________
def separe
    print "-"*80
    puts
end

